<?php

/**
 * @file
 * Customize confirmation screen after successful submission.
 *
 * This file may be renamed "webform-confirmation-[nid].tpl.php" to target a
 * specific webform e-mail on your site. Or you can leave it
 * "webform-confirmation.tpl.php" to affect all webform confirmations on your
 * site.
 *
 * Available variables:
 * - $node: The node object for this webform.
 * - $confirmation_message: The confirmation message input by the webform author.
 * - $sid: The unique submission ID of this submission.
 */
module_load_include('inc', 'webform', 'includes/webform.submissions');
$submission = webform_get_submission($node->nid, $sid);
$account = user_load($submission->uid);

$form_title = $submission->data['23'][0];
$payment_type = $submission->data['21'][0];
$name = $submission->data['5'][0];
$email = $submission->data['7'][0];
$amount = $submission->data['22'][0];
?>


<div class="webform-confirmation">
  <p>Thank you, your submission has been received.  You registration and request will not be processed until payment is received.  <br/>Your total cost will be $<?php print $amount; ?>.</p>
  <?php if ($payment_type == 'check'): ?>
    Make check payable to the <strong>University of Kentucky</strong> and mail to:<br/>
    Office of State Archaeology<br/>
    University of Kentucky<br/>
    1020A Export St<br/>
    Lexington, KY. 40506<br/><br/>
    <em>Your registration is not complete until your check is received.</em>
  <?php elseif ($payment_type == 'cc_online'): ?>
    To complete your registration please click below on "Submit Payment" to pay by credit card.<br/>
    <form name="SkipjackVposGenerator" method="post" action="https://payments.skipjack.com/FormBuilder/VPOS.aspx">
      <input type="hidden" name="VposContent" value="">
      <input type="hidden" name="formtitle" value="<?php print $form_title; ?>"/>
      <input type="hidden" name="transactionamount" value="<?php print $amount; ?>"/>
      <input type="hidden" name="contactname" value="<?php print $name; ?>"/>
      <input type="hidden" name="email" value="<?php print $email; ?>"/>
      <input type="submit" name="DonateButton" value="Submit Payment" >
    </form>
  <?php elseif ($payment_type == 'cc_phone'): ?>
    To pay by phone, please call 859-257-1944.
  <?php endif; ?>
</div>

<div class="links">
  <a href="<?php print url('node/'. $node->nid) ?>"><?php print t('Go back to the form') ?></a>
</div>
