<?php

/**
 * @file
 * Customize confirmation screen after successful submission.
 *
 * This file may be renamed "webform-confirmation-[nid].tpl.php" to target a
 * specific webform e-mail on your site. Or you can leave it
 * "webform-confirmation.tpl.php" to affect all webform confirmations on your
 * site.
 *
 * Available variables:
 * - $node: The node object for this webform.
 * - $confirmation_message: The confirmation message input by the webform author.
 * - $sid: The unique submission ID of this submission.
 */
module_load_include('inc', 'webform', 'includes/webform.submissions');
$submission = webform_get_submission($node->nid, $sid);
$account = user_load($submission->uid);
$name = array();
foreach (array(2,3,4) as $key) {
  if (!empty($submission->data[$key][0])) {
    $name[] = $submission->data[$key][0];
  }
}

$cost = 0;
switch ($submission->data[1][0]) {
  case 'faculty':
    $cost = 70;
    break;
  case 'graduate':
    $cost = 35;
    break;
  case 'professional':
    $cost = 70;
    break;
}
if (!empty($submission->data[29][0])) {
  $cost += intval($submission->data[29][0]);
}
foreach ($submission->data[41] as $key) {
  switch ($key) {
    case 'mountaintop':
      $cost += 35;
      break;
    case 'spiritky':
      $cost += 25;
      break;
    case 'localag':
      $cost += 20;
      break;
    case 'walking':
      $cost += 10;
      break;
    case 'mammoth':
      $cost += 35;
      break;
  }
}


?>

<div class="webform-confirmation">
  <?php if ($cost == 0): ?>
    <p>Thank you for registering! Undergraduate students and UK Faculty, Staff, and Students can attend DOPE 2015 free of charge. Welcome!</p>
  <?php else: ?>
    <p>Thank you for registering! Your total cost will be $<?php print $cost; ?>.</p>
      <h2>Pay by Credit Card</h2>
      <p>To complete your registration please click below on "Complete Online Payment" to pay by credit card.</p>
      <form name="SkipjackVposGenerator" method="post" action="https://payments.skipjack.com/FormBuilder/VPOS.aspx">
        <input type="hidden" name="VposContent" value="">
        <input type="hidden" name="firstname" value="<?php print $submission->data[2][0]; ?>"/>
        <input type="hidden" name="lastname" value="<?php print $submission->data[3][0]; ?>"/>
        <input type="hidden" name="email" value="<?php print $submission->data[8][0]; ?>"/>
        <input type="hidden" name="transactionamount" value="<?php print $cost; ?>"/>
        <input type="submit" name="DonateButton" value="Complete Online Payment" >
      </form>

      <p><em>Your registration is not complete until your payment is received.</em></p>
  <?php endif; ?>
</div>

<div class="links">
  <a href="<?php print url('node/'. $node->nid) ?>"><?php print t('Go back to the form') ?></a>
</div>
